mapper.select:
SELECT bno, title, content, id, readcnt, TO_CHAR(regdate, 'yyyy-mm-dd') regdate, 
TO_CHAR(updatedate, 'yyyy-mm-dd') updatedate FROM tbl_board 
WHERE bno = #{bno}

mapper.update:
UPDATE tbl_board 
SET title = #{title}, content=#{content}, updatedate = SYSDATE 
WHERE bno = #{bno} AND pw =#{pw}
